import React, {useState} from "react";
import {Modal, Button, Input} from 'antd';
import '../App.css';

const CoordInsert = () => {
    // constructor(props) {
    //     super(props);
    //
    //     this.state = {
    //         visibleModal: false,
    //         latModal: 37.5,
    //         longModal: 50,
    //         zoomModal: 10,
    //     };
    // }
    const [visibleModal, setVisibleModal] = useState(false)
    const [latModal, setLatModal] = useState(37.5)
    const [longModal, setLongModal] = useState(50)
    const [zoomModal, setZoomModal] = useState(10)


    const showModal = () => {
        setVisibleModal(true)
    };

    const handleOk = (e) => {
        this.props.onllzChange(latModal, longModal, zoomModal);
        setVisibleModal(false)
    };

    const handleCancel = e => {
        setVisibleModal(false)
    };

    const onLatChange = (e) => {
        setLatModal(e.target.value)
    }

    const onLongChange = (e) => {
        longModal: setLongModal(e.target.value)
    }

    const onZoomChange = (e) => {
        setZoomModal(e.target.value)
    }


        return (
            <div className="button-widget">
                <Button type="primary" onClick={this.showModal}>
                    Open Modal
                </Button>
                <Modal
                    title="Basic Modal"
                    visible={visibleModal}
                    onOk={handleOk}
                    onCancel={handleCancel}
                >
                    <Input type="number" addonBefore="Lat" defaultValue={37.5}
                           onChange={onLatChange}/>
                    <br/><br/>
                    <Input type="number" addonBefore="Long" defaultValue={50}
                           onChange={onLongChange}/>
                    <br/><br/>
                    <Input type="number" addonBefore="Zoom" defaultValue={10}
                           onChange={onZoomChange}/>
                </Modal>
            </div>
        );
};

export default CoordInsert;
