import React from 'react';
import './App.css';

import {Map, TileLayer, Marker, Popup} from 'react-leaflet'
import L from 'leaflet'

import {
    CardText, CardBody,
    CardTitle, Button,
    Form, FormGroup,
    Label, Input
} from 'reactstrap';
export const pointerIcon = new L.Icon({
    iconUrl: 'data:image/png;base64,',
    iconSize: [25, 41],
    iconAnchor: [12.5, 41],
    popupAnchor: [0, -41],
})

class App extends React.Component {
    state = {
        location: {
            lat: 50.433300,
            lng: 30.416700,
        },
        haveUserLocationFalse: false,
        zoom: 2,
    }

    componentDidMount() {
        navigator.geolocation.getCurrentPosition((position) => {
            this.setState({
                location: {
                    lat: position.coords.latitude,
                    lng: position.coords.longitude
                },
                haveUserLocationFalse: true,
                zoom: 13,
            })
        }, () => {
            console.log('uh, oh... they didnt give us their location...')
            fetch('http://ip-api.com/json/')
                .then(res => res.json())
                .then(location => {
                    this.setState({
                        location: {
                            lat: location.lat,
                            lng: location.lon
                        },
                        haveUserLocationFalse: true,
                        zoom: 13
                    })
                })
        });
    }

    render() {
        const position = [this.state.location.lat, this.state.location.lng]
        return (
            <div className="map">
                <Map className="map" center={position} zoom={this.state.zoom}>
                    <TileLayer
                        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {
                        this.state.haveUserLocationFalse
                            ? <Marker position={position} icon={pointerIcon}>
                                <Popup>
                                    A pretty CSS3 popup. <br/> Easily customizable.
                                </Popup>
                            </Marker>
                            : ""
                    }
                </Map>
                <CardBody className="message-form">
                    <CardTitle>Welcome to GuestM.app</CardTitle>
                    <CardText>Leave a message with your location!</CardText>
                    <CardText>Thanks for stopping by!</CardText>
                    <Form>
                        <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
                        </FormGroup>
                    </Form>
                </CardBody>
            </div>
        )
    }
}

export default App
