import React from 'react';
import './App.css';

import {Map, TileLayer, Marker, Popup} from 'react-leaflet'
import L from 'leaflet'
import Joi from 'joi'

import {
    CardText, CardBody,
    CardTitle, Button,
    Form, FormGroup,
    Label, Input
} from 'reactstrap';

import { getMessages, getLocation, sendMessage } from "./API";

import messageIcon from './message-location.svg'
import myIcon from './my-location.svg'

const API_URL = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/v1/messages' : 'production_url_here'

export const messagesIcon = new L.Icon({
    iconUrl: messageIcon,
    iconSize: [35, 38],
    iconAnchor: [13.5, 41],
    popupAnchor: [0, -41],
})

export const myIcons = new L.Icon({
    iconUrl: myIcon,
    iconSize: [35, 38],
    iconAnchor: [13.5, 41],
    popupAnchor: [0, -41],
})

const schema = Joi.object().keys({
    name: Joi.string().min(1).max(500).required(),
    message: Joi.string().min(1).max(500).required(),
})


class App extends React.Component {
    state = {
        location: {
            lat: 50.433300,
            lng: 30.416700,
        },
        haveUserLocation: false,
        zoom: 2,
        userMessage: {
            name: '',
            message: ''
        },
        sendingMessage: false,
        sentMessage: false,
        messages: []
    }

    componentDidMount() {
        getMessages()
            .then(messages => {
                this.setState({
                    messages
                })
            })

        getLocation()
            .then(location => {
                this.setState({
                    location,
                    haveUserLocation: true,
                    zoom: 13
                })
            })
    }

    formIsValid = () => {
        const userMessage = {
            name: this.state.userMessage.name,
            message: this.state.userMessage.message
        }
        const result = schema.validate(userMessage)

        if (this.state.haveUserLocation) {
            if (!result.error) {
                return true
            }
            return false
        }
    }

    formSubmitted = (event) => {
        event.preventDefault()
        this.setState({
            sendingMessage: true
        })
        if (this.formIsValid()) {
        }
    }

    valueChanged = (event) => {
        const {name, value} = event.target
        this.setState((prevState) => ({
            userMessage: {
                ...prevState.userMessage,
                [name]: value
            }
        }))
    }

    render() {
        const position = [this.state.location.lat, this.state.location.lng]
        return (
            <div className="map">
                <Map className="map" center={position} zoom={this.state.zoom}>
                    <TileLayer
                        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {
                        this.state.haveUserLocation
                            ? <Marker position={position} icon={myIcons}>
                                <Popup>
                                    A pretty CSS3 popup. <br/> Easily customizable.
                                </Popup>
                            </Marker>
                            : ""
                    }
                    {
                        this.state.messages.map(message => {
                            return (<Marker key={message._id} position={[message.latitude, message.longitude]} icon={messagesIcon}>
                                <Popup>
                                    <p><em>{message.name}</em>{message.message}</p>
                                    { message.otherMessages ? message.otherMessages.map(message => <p key={message._id}><em>{message.name}</em> : {message.message}</p>) : "" }
                                </Popup>
                            </Marker>)
                        })
                    }
                </Map>
                <CardBody className="message-form">
                    <CardTitle>Welcome to GuestM.app</CardTitle>
                    <CardText>Leave a message with your location!</CardText>
                    <CardText>Thanks for stopping by!</CardText>
                    {!this.state.sendingMessage && !this.state.sentMessage && this.state.haveUserLocation ?
                        <Form onSubmit={this.formSubmitted}>
                            <FormGroup>
                                <Label for="name">Name</Label>
                                <Input
                                    onChange={this.valueChanged}
                                    type="text"
                                    name="name"
                                    id="name"
                                    placeholder="Enter your name"/>
                            </FormGroup>
                            <FormGroup>
                                <Label for="message">Message</Label>
                                <Input
                                    onChange={this.valueChanged}
                                    type="textarea"
                                    name="message"
                                    id="message"
                                    placeholder="Enter a message"/>
                            </FormGroup>
                            <Button type="submit" color="info" disabled={!this.formIsValid()}>Send</Button>
                        </Form>
                        : this.state.sendingMessage || !this.state.haveUserLocation ?
                            <video className="video" autoPlay loop
                                   src="https://media.giphy.com/media/XfIc8q8bPfU08fxcjR/giphy.mp4"/>
                            : <CardText>Thanks for submitting a message!</CardText>
                    }
                </CardBody>
            </div>
        )
    }
}

export default App
